                        Area = parts[0].Trim(),
                        Profession = parts[1].Trim(),
                        ButtonTitle = parts[2].Trim(),
                        Content = parts[3].Trim()
                    });
                }
            }

            if (prompts.Any())
            {
                context.SystemPrompts.AddRange(prompts);
